mapper.select:
select
PSN_TRAT_CLCT_ID as psnTratClctId, PSN_NO as psnNo, INSUTYPE as insutype, TRAT_CLCT_TYPE as tratClctType, BEGN_YM as begnYm, END_YM as endYm, TRAT_CLCT_MONS as tratClctMons, VALI_FLAG as valiFlag, MEMO as memo, INSU_ORG as insuOrg, OPT_CHNL as optChnl, OPTINS as optins, OPTER as opter, OPTER_NAME as opterName, OPT_TIME as optTime, CRTE_OPTINS as crteOptins, CRTER as crter, CRTER_NAME as crterName, CRTE_TIME as crteTime, UPDT_TIME as updtTime, RID as rid, PSN_INSU_RLTS_ID as psnInsuRltsId, PSN_INSU_MGT_EID as psnInsuMgtEid
from psn_trat_year_d




        and PSN_TRAT_CLCT_ID = #{psnTratClctId}



        and PSN_NO = #{psnNo}



        and INSUTYPE = #{insutype}



        and TRAT_CLCT_TYPE = #{tratClctType}



        and BEGN_YM = #{begnYm}



        and END_YM = #{endYm}



        and TRAT_CLCT_MONS = #{tratClctMons}



        and VALI_FLAG = #{valiFlag}



        and MEMO = #{memo}



        and INSU_ORG = #{insuOrg}



        and OPT_CHNL = #{optChnl}



        and OPTINS = #{optins}



        and OPTER = #{opter}



        and OPTER_NAME = #{opterName}



        and OPT_TIME = #{optTime}



        and CRTE_OPTINS = #{crteOptins}



        and CRTER = #{crter}



        and CRTER_NAME = #{crterName}



        and CRTE_TIME = #{crteTime}



        and UPDT_TIME = #{updtTime}



        and RID = #{rid}



        and PSN_INSU_RLTS_ID = #{psnInsuRltsId}



        and PSN_INSU_MGT_EID = #{psnInsuMgtEid}
